mapper.update:

UPDATE BOARD
	SET READ_COUNT = READ_COUNT+1
WHERE BOARD_ID = #{boardId}

mapper.select:

SELECT PASSWORD
FROM BOARD
WHERE BOARD_ID = #{boardId}

